import React, { useState } from 'react';

const Grounds = () => {
  const [selectedGround, setSelectedGround] = useState(null);
  const [showBookingForm, setShowBookingForm] = useState(false);
  const [bookingDetails, setBookingDetails] = useState({
    date: '',
    time: '',
    contact: '',
    // Add more fields as needed for your booking
  });

  const openWhatsApp = (phoneNumber, message) => {
    const url = `https://wa.me/${phoneNumber}?text=${encodeURIComponent(message)}`;
    window.open(url, '_blank');
  };

  const handleBookingConfirmation = () => {
    // Handle booking confirmation here
    // You can access the booking details from the state (bookingDetails)
    // Perform actions like sending data to a server, etc.
    console.log('Booking confirmed:', bookingDetails);
    // Optionally, you can open WhatsApp with booking details
    const whatsappMessage = `Booking Details: Date - ${bookingDetails.date}, Time - ${bookingDetails.time}, Contact - ${bookingDetails.contact}`;
    openWhatsApp(+353894274383, whatsappMessage);
  };

  return (
    <div className='bookground'>
      <h1>Grounds</h1>
      {/* Your Grounds Display Section */}
      {/* For each ground card, you can have a button to trigger the booking form */}
      {/* Example: */}
      {/* Card Details */}
      <button onClick={() => {
        setSelectedGround(/* Pass selected ground data here */);
        setShowBookingForm(true);
      }}>
        Book Now
      </button>

      {/* Booking Form */}
      {showBookingForm && selectedGround && (
        <div className="booking-form">
          <h2>Book {selectedGround.name}</h2>
          {/* Your booking form components go here */}
          {/* Example: input fields for date, time, contact, etc. */}
          <input
            type="text"
            placeholder="Date"
            value={bookingDetails.date}
            onChange={(e) => setBookingDetails({ ...bookingDetails, date: e.target.value })}
          />
          <input
            type="text"
            placeholder="Time"
            value={bookingDetails.time}
            onChange={(e) => setBookingDetails({ ...bookingDetails, time: e.target.value })}
          />
          <input
            type="text"
            placeholder="Contact"
            value={bookingDetails.contact}
            onChange={(e) => setBookingDetails({ ...bookingDetails, contact: e.target.value })}
          />
          <button onClick={handleBookingConfirmation}>
            Confirm Booking <i className="fab fa-whatsapp"></i>
          </button>
        </div>
      )}
    </div>
  );
};

export default Grounds;
