import React, { useState } from 'react';
import './Grounds.css';
import { bookgrounds } from '../data/data';

const Grounds = () => {
  const [showBookingModal, setShowBookingModal] = useState(false);
  const [selectedGround, setSelectedGround] = useState(null);

  const openWhatsApp = (phoneNumber, message) => {
    const url = `https://wa.me/${phoneNumber}?text=${encodeURIComponent(message)}`;
    window.open(url, '_blank');
  };

  const toggleBookingModal = (ground) => {
    setShowBookingModal(!showBookingModal);
    setSelectedGround(ground);
  };

  return (
    <div className='headbook'>
      {/* ...Other content */}
      {bookgrounds.map((item) => (
        <div className="card" key={item.id}>
          {/* ...Card content */}
          <div className="card-footer">
            <p>Rating: {item.rating}/5</p>
            <button onClick={() => toggleBookingModal(item)}>Book Now <i className="fab fa-whatsapp"></i></button>
          </div>
        </div>
      ))}

      {/* Booking Modal */}
      {showBookingModal && selectedGround && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={() => setShowBookingModal(false)}>&times;</span>
            <h2>Book {selectedGround.name}</h2>
            {/* Your booking form components go here */}
            {/* Example: input fields for date, time, contact, etc. */}
            <button onClick={() => openWhatsApp(+353894274383, 'I want to book this ground.')}>
              Confirm Booking <i className="fab fa-whatsapp"></i>
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Grounds;
